import Vue from 'vue'
import Vuex from 'vuex'
import {dataService} from '../shared'

Vue.use(Vuex);

const state={
  heroes:[],
};
const mutations={};
const actions={
  async getHeroesAction({commit}){
    const heroes=await dataService.getHeroes();
    commit('getHeroes');
  }
};
const getters={};

export default new Vuex.Store({
  state,
  mutations,
  actions,
  getters,
})
